// class that generates acronyms by the following algorithm:
// Generate acronym by calling generateAcronym()
// Acronym is generated by randomly selecting a letter from the alphabet
// and adding it to the acronym. If dots are to be added, a dot is added
// letters are selected by the defined distribution

#include <string>
#include <random>
#include <iostream>

#include "AcronymGenerator.h"

using namespace std;

#define ALPHABET_SIZE 26

string AcronymGenerator::generateAcronym(int length, bool addDots) {
  random_device rd;
  mt19937 gen(rd());
  discrete_distribution<> letterDist(probs, probs + ALPHABET_SIZE);
  string acronym = "";
  for (int i = 0; i < length; i++) {
    char letter = letters[letterDist(gen)];
    acronym += letter;
    if (addDots && i < length - 1) {
      acronym += ".";
    }
  }
  return acronym;
}
